const input = $json || {};
const produits = Array.isArray(input.produits) ? input.produits : (Array.isArray(input.items) ? input.items : []);
function num(v, d=0){ const n=Number(v); return Number.isFinite(n)?n:d; }
const out = {
  numero_facture: String(input.numero_facture || input.id || input.invoiceNumber || `AUTO-${Date.now()}`),
  date_facture: String(input.date_facture || input.invoiceDate || new Date().toISOString().slice(0,10)),
  nom_client: input.nom_client || input.client?.name || 'Client',
  montant_ttc: num(input.montant_ttc, NaN),
  payment_method: input.payment_method || input.payment?.method || 'card',
  vendeuse: String(input.vendeuse || input.vendorName || 'Sylvie'),
  vendorId: String((input.vendorId || input.vendor_id || input.vendeuse || 'Sylvie')).toLowerCase(),
  produits: Array.isArray(produits) && produits.length > 0 ? produits.map(p=>({
    nom: p.nom || p.name || 'Produit',
    quantite: num(p.quantite ?? p.quantity ?? p.qty ?? 1, 1),
    prix_ttc: num(p.prix_ttc ?? p.unitPriceTTC ?? p.priceTTC ?? p.price ?? 0, 0),
    remise: num(p.remise ?? p.discount ?? 0, 0)
  })) : [{
    nom: 'Service',
    quantite: 1,
    prix_ttc: num(out.montant_ttc || 0, 0),
    remise: 0
  }]
};
// Protection contre les données invalides
if (!Array.isArray(out.produits)) out.produits = [];
if (out.produits.length === 0) {
  out.produits = [{
    nom: 'Service',
    quantite: 1,
    prix_ttc: num(out.montant_ttc || 0, 0),
    remise: 0
  }];
}
if (!(out.montant_ttc > 0)) {
  const ttcFromLines = out.produits.reduce((s,p)=> s + p.quantite * p.prix_ttc * (1 - (p.remise||0)), 0);
  out.montant_ttc = +ttcFromLines.toFixed(2);
}
return [{ json: out }];

// Build le payload final attendu par l'app Caisse (clé/valeurs propres)
const d = $json || {};
function num(v, d=0){ const n=Number(v); return Number.isFinite(n)?n:d; }
const produits = Array.isArray(d.produits) ? d.produits.map(p=>({
  nom: p.nom || 'Produit',
  quantite: num(p.quantite,1),
  prix_ttc: num(p.prix_ttc,0),
  remise: num(p.remise,0)
})) : [];
const out = {
  numero_facture: String(d.numero_facture || `AUTO-${Date.now()}`),
  date_facture: String(d.date_facture || new Date().toISOString().slice(0,10)),
  nom_client: d.nom_client || 'Client',
  montant_ttc: num(d.montant_ttc, produits.reduce((s,p)=> s + p.quantite*p.prix_ttc*(1-(p.remise||0)), 0)),
  payment_method: d.payment_method || 'card',
  vendeuse: String(d.vendeuse || 'Sylvie'),
  vendorId: String((d.vendorId || 'sylvie')).toLowerCase(),
  produits
};
// Validations avec valeurs par défaut au lieu d'erreurs
if (!out.numero_facture || out.numero_facture === 'undefined') {
  out.numero_facture = `AUTO-${Date.now()}`;
}
if (!(out.montant_ttc > 0)) {
  if (produits.length > 0) {
    out.montant_ttc = +produits.reduce((s,p)=> s + p.quantite*p.prix_ttc*(1-(p.remise||0)), 0).toFixed(2);
  } else {
    out.montant_ttc = 0;
  }
}
if (!produits.length) {
  // Si pas de produits, on crée un produit par défaut
  out.produits = [{
    nom: 'Service',
    quantite: 1,
    prix_ttc: out.montant_ttc,
    remise: 0
  }];
}
return [{ json: out }];
